import React, { useState } from 'react';
import {
  Container,
  Paper,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Button,
  Stack,
  IconButton,
  Chip,
} from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import EmployeeDialog from '../components/EmployeeDialog';
import TransferDialog from '../components/TransferDialog';
import { useDepartments } from '../context/DepartmentContext';

interface Employee {
  id: string;
  account: string;
  position: string;
  department: string;
  hireDate: string;
  status: 'Active' | 'Inactive';
}

interface Account {
  email: string;
  firstName: string;
  lastName: string;
}

// Sample accounts data (you should integrate this with your Accounts page)
const sampleAccounts: Account[] = [
  {
    email: 'Xavier@Xmen.com',
    firstName: 'James',
    lastName: 'Xavier',
  },
  {
    email: 'Jane@gmail.com',
    firstName: 'Jane',
    lastName: 'Frost',
  },
  {
    email: 'Beast@yahoo.com',
    firstName: 'Beast',
    lastName: 'Manhattan',
  },
];

const initialEmployees: Employee[] = [
  {
    id: 'EMP001',
    account: 'Xavier@Xmen.com',
    position: 'Professor',
    department: 'Engineering',
    hireDate: '2025-01-01',
    status: 'Active',
  },
  {
    id: 'EMP002',
    account: 'Jane@gmail.com',
    position: 'Designer',
    department: 'Marketing',
    hireDate: '2025-02-01',
    status: 'Active',
  },
];

const Employees = () => {
  const [employees, setEmployees] = useState<Employee[]>(initialEmployees);
  const { departments, updateEmployeeCount } = useDepartments();
  const [dialogOpen, setDialogOpen] = useState(false);
  const [transferDialogOpen, setTransferDialogOpen] = useState(false);
  const [selectedEmployee, setSelectedEmployee] = useState<Employee | undefined>();
  const [dialogMode, setDialogMode] = useState<'add' | 'edit'>('add');

  // Function to generate next employee ID
  const getNextEmployeeId = () => {
    const maxId = employees.reduce((max, employee) => {
      const num = parseInt(employee.id.replace('EMP', ''));
      return num > max ? num : max;
    }, 0);
    return `EMP${String(maxId + 1).padStart(3, '0')}`;
  };

  const handleAddClick = () => {
    setSelectedEmployee(undefined);
    setDialogMode('add');
    setDialogOpen(true);
  };

  const handleEditClick = (employee: Employee) => {
    setSelectedEmployee(employee);
    setDialogMode('edit');
    setDialogOpen(true);
  };

  const handleDeleteClick = (employeeToDelete: Employee) => {
    updateEmployeeCount(employeeToDelete.department, -1);
    setEmployees((prevEmployees) =>
      prevEmployees.filter((emp) => emp.id !== employeeToDelete.id)
    );
  };

  const handleTransferClick = (employee: Employee) => {
    setSelectedEmployee(employee);
    setTransferDialogOpen(true);
  };

  const handleDialogClose = () => {
    setDialogOpen(false);
    setSelectedEmployee(undefined);
  };

  const handleTransferDialogClose = () => {
    setTransferDialogOpen(false);
    setSelectedEmployee(undefined);
  };

  const handleSaveEmployee = (employee: Employee) => {
    if (dialogMode === 'add') {
      setEmployees((prevEmployees) => [...prevEmployees, employee]);
      updateEmployeeCount(employee.department, 1);
    } else if (selectedEmployee) {
      if (selectedEmployee.department !== employee.department) {
        updateEmployeeCount(selectedEmployee.department, -1);
        updateEmployeeCount(employee.department, 1);
      }
      setEmployees((prevEmployees) =>
        prevEmployees.map((e) => (e.id === employee.id ? employee : e))
      );
    }
  };

  const handleTransfer = (newDepartment: string) => {
    if (selectedEmployee) {
      updateEmployeeCount(selectedEmployee.department, -1);
      updateEmployeeCount(newDepartment, 1);
      setEmployees((prevEmployees) =>
        prevEmployees.map((e) =>
          e.id === selectedEmployee.id
            ? { ...e, department: newDepartment }
            : e
        )
      );
    }
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Paper sx={{ p: 2 }}>
        <Typography variant="h6" gutterBottom>
          EMPLOYEES
        </Typography>
        <TableContainer>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Employee ID</TableCell>
                <TableCell>Account</TableCell>
                <TableCell>Position</TableCell>
                <TableCell>Department</TableCell>
                <TableCell>Hire Date</TableCell>
                <TableCell>Status</TableCell>
                <TableCell>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {employees.map((employee) => (
                <TableRow key={employee.id}>
                  <TableCell>{employee.id}</TableCell>
                  <TableCell>{employee.account}</TableCell>
                  <TableCell>{employee.position}</TableCell>
                  <TableCell>{employee.department}</TableCell>
                  <TableCell>{employee.hireDate}</TableCell>
                  <TableCell>
                    <Chip
                      label={employee.status}
                      color={employee.status === 'Active' ? 'success' : 'error'}
                    />
                  </TableCell>
                  <TableCell>
                    <Stack direction="row" spacing={1}>
                      <Button
                        variant="contained"
                        size="small"
                        color="info"
                        onClick={() => handleTransferClick(employee)}
                      >
                        Transfer
                      </Button>
                      <IconButton
                        size="small"
                        color="primary"
                        onClick={() => handleEditClick(employee)}
                      >
                        <EditIcon />
                      </IconButton>
                      <IconButton
                        size="small"
                        color="error"
                        onClick={() => handleDeleteClick(employee)}
                      >
                        <DeleteIcon />
                      </IconButton>
                    </Stack>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
        <Button
          variant="contained"
          color="primary"
          sx={{ mt: 2 }}
          onClick={handleAddClick}
        >
          Add Employee
        </Button>
      </Paper>

      <EmployeeDialog
        open={dialogOpen}
        onClose={handleDialogClose}
        onSave={handleSaveEmployee}
        employee={selectedEmployee}
        mode={dialogMode}
        departments={departments}
        accounts={sampleAccounts}
        nextEmployeeId={getNextEmployeeId()}
      />

      {selectedEmployee && (
        <TransferDialog
          open={transferDialogOpen}
          onClose={handleTransferDialogClose}
          onTransfer={handleTransfer}
          departments={departments}
          currentDepartment={selectedEmployee.department}
        />
      )}
    </Container>
  );
};

export default Employees;
