import React from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Chip,
  IconButton,
  Box,
} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import { Request, Employee } from '../services/storageService';

interface EmployeeRequestsDialogProps {
  open: boolean;
  onClose: () => void;
  employee: Employee;
  requests: Request[];
  onEditRequest: (request: Request) => void;
  onDeleteRequest: (request: Request) => void;
}

const EmployeeRequestsDialog: React.FC<EmployeeRequestsDialogProps> = ({
  open,
  onClose,
  employee,
  requests,
  onEditRequest,
  onDeleteRequest,
}) => {
  const employeeRequests = requests.filter(req => req.employeeId === employee.id);

  const formatItems = (items: Request['items']) => {
    return items.map(item => `${item.name} x ${item.quantity}`).join(', ');
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'Approved':
        return 'success';
      case 'Disapproved':
        return 'error';
      default:
        return 'warning';
    }
  };

  return (
    <Dialog open={open} onClose={onClose} maxWidth="md" fullWidth>
      <DialogTitle>
        Request History - {employee.id}
      </DialogTitle>
      <DialogContent>
        <TableContainer component={Paper}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Type</TableCell>
                <TableCell>Description</TableCell>
                <TableCell>Request Date</TableCell>
                <TableCell>Items</TableCell>
                <TableCell>Status</TableCell>
                <TableCell>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {employeeRequests.map((request) => (
                <TableRow key={request.id}>
                  <TableCell>{request.type}</TableCell>
                  <TableCell>{request.description}</TableCell>
                  <TableCell>{request.requestDate}</TableCell>
                  <TableCell>{formatItems(request.items)}</TableCell>
                  <TableCell>
                    <Chip
                      label={request.status}
                      color={getStatusColor(request.status)}
                    />
                  </TableCell>
                  <TableCell>
                    {request.status === 'Pending' && (
                      <Box>
                        <IconButton
                          size="small"
                          onClick={() => onEditRequest(request)}
                          color="primary"
                        >
                          <EditIcon />
                        </IconButton>
                        <IconButton
                          size="small"
                          onClick={() => onDeleteRequest(request)}
                          color="error"
                        >
                          <DeleteIcon />
                        </IconButton>
                      </Box>
                    )}
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </DialogContent>
    </Dialog>
  );
};

export default EmployeeRequestsDialog;
